    private void btn_enviarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_enviarActionPerformed
        String letra=(txt_digitado.getText()), conv_i;
        int i=6;
        
        if(letra.equals("h")){
            lbl_2.setText("H");
        }
        else{
            if(letra.equals("c")){
                lbl_1.setText("C");
            }
            else{
                if(letra.equals("a")){
                    lbl_3.setText("A");
                }
                else{
                    if(letra.equals("e")){
                        lbl_5.setText("E");
                    }
                    else{
                        if(letra.equals("v")){
                            lbl_4.setText("V");
                        }
                        else{
                            i--;
                            conv_i = "" + i;
                            lbl_tent.setText(conv_i);
                        }
                    }
                }
            }
        }
        letra+= " - ";
        lbl_letras.setText(letra);
    }//GEN-LAST:event_btn_enviarActionPerformed

